mapper.select:
SELECT * FROM ${tableName} 
WHERE ${updateTimeField} >= #{startTime}

	AND ${updateTimeField} <= #{endTime}

ORDER BY ${updateTimeField} ASC LIMIT ${limit}

SELECT TOP ${limit} * FROM ${tableName} 
WHERE ${updateTimeField} >= #{startTime}

	AND ${updateTimeField} <= #{endTime}

ORDER BY ${updateTimeField} ASC

SELECT * FROM ${tableName} 
WHERE ${updateTimeField} >= to_date('${startTime}','yyyy-mm-dd hh24:mi:ss')

	AND ${updateTimeField} <= to_date('${endTime}','yyyy-mm-dd hh24:mi:ss')

AND rownum <= ${limit}
ORDER BY ${updateTimeField} ASC
